mapper.select:
select task.*, data_source.name as sourceName from task join data_source on task.source_id=data_source.id

    task.deleted = 0


        and data_source.id=#{sourceId}



        and task.job_id=#{jobId}



        and

            #{jid}




        and

            #{tid}




        and task.db_name=#{dbName}



        and task.table_name=#{tableName}



        and task.odps_project=#{odpsProject}



        and task.odps_table=#{odpsTable}



        and task.status = #{status} and task.stopped = 0



        and task.status in ('SCHEMA_FAILED', 'DATA_FAILED', 'VERIFICATION_FAILED') and task.stopped = 0



        and task.stopped = 1



        and task.stopped = 0 and task.status in ('SCHEMA_DOING', 'DATA_DOING', 'VERIFICATION_DOING', 'SCHEMA_DONE', 'DATA_DONE')






            task.${o.name} ${o.order}



        order by task.id desc




    limit #{offset}, #{pageSize}

select count(*) from task

    task.deleted = 0


       and source_id=#{sourceId}



        and job_id=#{jobId}



        and

            #{jid}




        and

            #{tid}




        and db_name=#{dbName}



        and table_name=#{tableName}



        and odps_project=#{odpsProject}



        and odps_table=#{odpsTable}



        and status = #{status} and stopped = 0



        and status in ('SCHEMA_FAILED', 'DATA_FAILED', 'VERIFICATION_FAILED') and task.stopped = 0



        and stopped = 1



        and stopped = 0 and status in ('SCHEMA_DOING', 'DATA_DOING', 'VERIFICATION_DOING', 'SCHEMA_DONE', 'DATA_DONE')

mapper.update:
update
    task
set
    status=#{status}
where id=#{id};

update
    task
set
    sub_status=#{subStatus}
where id=#{id};

mapper.insert:
insert into task (id, job_id, source_id, db_id, table_id, db_name, table_name, odps_project, odps_table, type, status)
values (#{id}, #{jobId}, #{sourceId}, #{dbId}, #{tableId}, #{dbName}, #{tableName}, #{odpsProject}, #{odpsTable}, #{type},
        #{status}
        )

mapper.select:
select task.* from task join job on task.job_id=job.id
where (task.status='INIT' or task.restart=1) and task.stopped=0 and task.deleted=0 and job.stopped=0 and job.deleted=0a
order by task.restart desc, task.job_id limit #{limit}

select id, job_id, db_id, db_name, table_id, table_name from task
#{tableId}
and status not in ('SCHEMA_FAILED', 'DATA_FAILED', 'VERIFICATION_FAILED', 'DONE') and deleted = 0

select a.id, a.job_id, a.db_id, a.db_name, a.table_id, a.table_name from task a join task_partition b on a.id = b.task_id
#{ptId}
and a.status not in ('SCHEMA_FAILED', 'DATA_FAILED', 'VERIFICATION_FAILED', 'DONE') and deleted = 0

select id, job_id, db_id, db_name, table_id, table_name
from task
where job_id=#{jobId} and status not in ('SCHEMA_FAILED', 'DATA_FAILED', 'VERIFICATION_FAILED', 'DONE');

select distinct(job_id) from task


        db_name=#{dbName}



        and table_name=#{tableName}



        and odps_project=#{dstOdpsProject}



        and odps_table=#{dstOdpsTable}



        and id in (
          select task_id from task_partition where partition_id in (select id from partition_model where `value` like CONCAT('%', #{ptValue}, '%'))
        )
